import React, {useState} from 'react';
import {useLocation} from 'react-router-dom';
import urlFromTemplate from "../config/url";
import {ENDPOINTS} from "../config/endpoints";
import axios from "axios";

const Project = () => {
    const location = useLocation();
    const project = location.state.project;
    const [modalOpen, setModalOpen] = useState(false);
    const [amount, setAmount] = useState('');

    const handleModalSubmit = async () => {
        if (!amount || isNaN(amount) || amount <= 0) {
            alert('Please enter a valid amount.');
            return;
        }
        const apiUrl = urlFromTemplate(ENDPOINTS.PROJECT) + `/invest/${project.id}`;
        try {
            const response = await axios.post(apiUrl, {amount}, {
                withCredentials: true,
                headers: {
                    'Accept': 'application/json',
                    'Access-Control-Allow-Credentials': 'true',
                    'Authorization': localStorage.getItem('Token'),
                },
            })
            await localStorage.setItem('User', JSON.stringify(response.data));
            setModalOpen(false);
            setAmount('');
        } catch (error) {
            alert(error.response.data.message);
        }
    };

    return (<>
        <div>
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            <p>Pool Sum: {project.poolSum}</p>
            <button style={{width: '300px', marginTop: '10px'}}>
                <h3 style={{margin: '5px'}} onClick={() => setModalOpen(true)}>Invest to a project</h3>
            </button>
        </div>
        <div
            style={{
                position: 'fixed',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
            }}
            onClick={() => setModalOpen(false)}
        >
            <div
                style={{
                    backgroundColor: 'white',
                    padding: '20px',
                    borderRadius: '10px',
                    width: '300px',
                    textAlign: 'center',
                }}
                onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside the modal
            >
                <h2 style={{marginTop: '0'}}>{'Project investment'}</h2>
                <h5 style={{margin: '0 0 5px 0', fontWeight: 'normal'}}>Imagine it's a payment API :)</h5>
                <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    style={{width: '100%', padding: '10px', marginBottom: '15px'}}
                    placeholder="Enter amount"
                />
                <button
                    onClick={handleModalSubmit}
                    style={{padding: '10px 20px', marginRight: '10px'}}
                >
                    Submit
                </button>
            </div>
        </div>
    </>);
}

export default Project;
