import React, { useState } from 'react';

const Profile = () => {
    // Parse user from localStorage
    const storedUser = localStorage.getItem('User');
    const user = storedUser ? JSON.parse(storedUser) : {};

    // State for editable fields
    const [login, setLogin] = useState(user.login || '');
    const [password, setPassword] = useState(''); // Password should not be pre-filled for security reasons
    const [about, setAbout] = useState(user.about || '');

    // Submit handler
    const handleSubmit = (e) => {
        e.preventDefault();
        // Here you can send the updated data to the server
        console.log({ login, password, about });
        alert('Profile updated successfully!'); // Replace with your desired feedback mechanism
    };

    return (
        <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px', border: '1px solid #ccc', borderRadius: '10px' }}>
            <h2>Profile</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontWeight: 'bold' }}>Login</label>
                    <input
                        type="text"
                        value={login}
                        onChange={(e) => setLogin(e.target.value)}
                        style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                        required
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontWeight: 'bold' }}>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                        required
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontWeight: 'bold' }}>About</label>
                    <textarea
                        value={about}
                        onChange={(e) => setAbout(e.target.value)}
                        style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                        rows="4"
                    />
                </div>
                <h3>Display Only</h3>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontWeight: 'bold' }}>Inviter ID</label>
                    <p>{user.inviterId || 'N/A'}</p>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontWeight: 'bold' }}>Balance</label>
                    <p>{user.balance || '0.00'}</p>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label style={{ display: 'block', fontWeight: 'bold' }}>Registration Time</label>
                    <p>{user.registrationTime || 'N/A'}</p>
                </div>
                <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px' }}>
                    Save Changes
                </button>
            </form>
        </div>
    );
};

export default Profile;
