import React, {useState} from 'react';
import {Typography} from "@mui/material";
import urlFromTemplate from "../config/url";
import {ENDPOINTS} from "../config/endpoints";
import axios from "axios";

const Profile = () => {

    const storedUser = localStorage.getItem('User');
    const [user, setUser] = useState(storedUser ? JSON.parse(storedUser) : {});
    const [login, setLogin] = useState(user.login || '');
    const [password, setPassword] = useState('');
    const [about, setAbout] = useState(user.about || '');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const apiUrl = urlFromTemplate(ENDPOINTS.USER);
        try {
            const response = await axios.put(apiUrl, {id: user.id, login, password, about}, {
                withCredentials: true,
                headers: {
                    'Accept': 'application/json',
                    'Access-Control-Allow-Credentials': 'true',
                    'Authorization': localStorage.getItem('Token'),
                },
            })
            setUser(response.data);
            setLogin(response.data.login);
            setPassword(response.data.password);
            setAbout(response.data.about);
            localStorage.setItem('User', JSON.stringify(response.data));
        } catch (error) {
            alert(error.response.data.message);
        }
        alert('Profile updated successfully!');
    };

    // Format registration time to a more readable format
    const formatDate = (isoString) => {
        const options = {year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit'};
        return new Date(isoString).toLocaleString('en-US', options);
    };

    return (
        <div style={{backgroundColor: '#f3f3f3', minHeight: '100vh'}}>
            <div style={{maxWidth: '600px', margin: '0 auto'}}>
                <Typography variant="h5" style={{marginTop: '20px', textAlign: 'center'}}>Profile</Typography>
                <form onSubmit={handleSubmit} style={{maxWidth: '600px'}}>
                    <div style={{marginBottom: '10px'}}>
                        <label style={{display: 'block', marginBottom: '5px'}}>Login</label>
                        <input
                            type="text"
                            value={login}
                            onChange={(e) => setLogin(e.target.value)}
                            style={{width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '5px'}}
                            required
                        />
                    </div>
                    <div style={{marginBottom: '10px'}}>
                        <label style={{display: 'block', marginBottom: '5px'}}>Password</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            style={{width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '5px'}}
                            required
                        />
                    </div>
                    <div style={{marginBottom: '10px'}}>
                        <label style={{display: 'block', marginBottom: '5px'}}>About</label>
                        <textarea
                            value={about}
                            onChange={(e) => setAbout(e.target.value)}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #ccc',
                                borderRadius: '5px',
                                resize: 'none',
                            }}
                            rows="4"
                        />
                    </div>
                    <div style={{marginBottom: '10px', display: 'flex', justifyContent: 'space-between'}}>
                        <span>Role:</span>
                        <span>{user.role === 'CLIENT' ? 'Client' : 'Admin'}</span>
                    </div>
                    {user.inviterLogin &&
                        <div style={{marginBottom: '10px', display: 'flex', justifyContent: 'space-between'}}>
                            <span>Inviter:</span>
                            <span>{user.inviterLogin}</span>
                        </div>
                    }
                    <div style={{marginBottom: '10px', display: 'flex', justifyContent: 'space-between'}}>
                        <span>Balance:</span>
                        <span>{user.balance || '0.00'}</span>
                    </div>
                    <div style={{marginBottom: '15px', display: 'flex', justifyContent: 'space-between'}}>
                        <span>Registration Time:</span>
                        <span>{user.registrationTime ? formatDate(user.registrationTime) : 'N/A'}</span>
                    </div>
                    <button
                        type="submit"
                        style={{
                            width: '100%',
                            padding: '10px',
                            backgroundColor: '#4CAF50',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            fontSize: '16px',
                            cursor: 'pointer',
                        }}
                    >
                        Update
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Profile;
