import React, {useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import './Auth.css'
import urlFromTemplate from "../config/url";
import {ENDPOINTS} from "../config/endpoints";
import axios from "axios";

const Registration = () => {

    const [errorMessage, setErrorMessage] = useState('');
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const loginUser = async (e) => {
        e.preventDefault();
        const apiUrl = urlFromTemplate(ENDPOINTS.USER) + '/login';
        try {
            const response = await axios.post(apiUrl, {login, password}, {
                withCredentials: true,
                headers: {
                    'Accept': 'application/json',
                    'Access-Control-Allow-Credentials': 'true'
                },
            })
            localStorage.setItem('Token', response.headers['authorization']);
            navigate('./profile/projects')
        } catch (error) {
            alert(error.response.data.message);
        }
    };

    return (
        <>
            <h1>Registration</h1>

            <form action='/api/v1/register' method="POST">
                <div>
                    <label htmlFor="inviterLogin">Inviter Login:</label>
                    <input type="text" id="inviterLogin" name="inviterLogin" required/>
                </div>
                <div>
                    <label htmlFor="login">Login:</label>
                    <input type="text" id="login" name="login" required/>
                </div>
                <div>
                    <label htmlFor="password">Password:</label>
                    <input type="password" id="password" name="password" required/>
                </div>
                <button type="submit">Register</button>
            </form>

            <div id="error-message">
                <div>{errorMessage}</div>
            </div>

            <div className="login-link">
                <Link to="../" style={{cursor: "pointer"}}>
                    Already have an account?
                </Link>
            </div>
        </>
    );
};

export default Registration;
