import React, {useState} from "react";
import {Link} from "react-router-dom";
import './Auth.css'
import urlFromTemplate from "../config/url";
import {ENDPOINTS} from "../config/endpoints";
import axios from "axios";

const ProjectPage = () => {

    const [projects, setProjects] = useState([]);

    const getProjects = async (e) => {
        e.preventDefault();
        const apiUrl = urlFromTemplate(ENDPOINTS.PROJECT);
        try {
            const response = await axios.get(apiUrl, {
                withCredentials: true,
                headers: {
                    'Accept': 'application/json',
                    'Access-Control-Allow-Credentials': 'true',
                    'Authorization': localStorage.getItem('Token'),
                },
            })
            setProjects(response.data);
        } catch (error) {
            alert(error.response.data.message);
        }
    };

    return (
        <>
            <h1>Project List</h1>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Description</th>
                    <th>Pool Sum</th>
                </tr>
                </thead>
                <tbody>
                {projects.map((project) => (
                    <tr key={project.id} /*onClick={() => {
                        window.location.href = `/diamond/projects/${project.id}`
                    }}*/ style={{cursor: 'pointer'}}>
                        <td>{project.id}</td>
                        <td>{project.title}</td>
                        <td>{project.description}</td>
                        <td>{project.poolSum.toLocaleString('en-US', {style: 'currency', currency: 'USD'})}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </>
    );
};

export default ProjectPage;
